  //   //posts.forEach(post => console.log(post));
  //   posts.forEach(post => console.log(post));
  // }, []);


  return (

    <div className = "Stream">
      {posts.map(post => (
        <div id = "post" className = "Post" key = {post.postID}>
          <Post post= {post}/>
        </div>
      ))}
    </div>
  );
}

export default Stream;
